mapper.select:
		SELECT bb.*
		FROM (SELECT aa.*, ROWNUM as rn
      		  FROM (SELECT quiz_content
                           ,quiz_id
                           ,quiz_score
                           ,subject_id
            		FROM quizbox
            		WHERE test_id = #{testId}
            		) aa
      		 ) bb
		WHERE bb.rn = #{page}

		SELECT bb.*
		FROM (SELECT aa.*, ROWNUM as rn
      		  FROM (SELECT a.example_num, a.text_content
            		FROM answers a JOIN quizbox b ON(a.quiz_id = b.quiz_id)
            		WHERE a.subject_id = #{subjectId} AND b.test_id = #{testId}
            		ORDER BY a.quiz_id, a.example_num
            		) aa
      		 ) bb

		WHERE bb.rn > (#{page}-1)*5 and bb.rn <= #{page}*5

SELECT COUNT(quiz_id) as quizCnt
FROM quizbox
WHERE test_id = #{testId}
