mapper.select:
		SELECT  COUNT(b.result_id) AS result_id
        FROM    test_targets a
                JOIN exam_results b 
                ON(a.test_target_id = b.test_target_id)
        WHERE   a.user_id = #{userId}
        AND     a.test_id = #{testId}

		SELECT  NVL(MAX(b.is_reexam),2) AS is_reexam
        FROM    test_targets a
                JOIN exam_results b 
                ON(a.test_target_id = b.test_target_id)
        WHERE   a.user_id = #{userId}
        AND     a.test_id = #{testId}

SELECT b.* FROM (SELECT a.* ,ROWNUM AS rn FROM (SELECT a.test_id ,a.test_date ,a.test_name ,a.subject_name ,c.name FROM tests a JOIN test_targets b ON(a.test_id = b.test_id) JOIN users c ON(b.user_id = c.user_id)
b.user_id = #{userId}
AND a.subject_name = #{filterData}
AND a.test_name LIKE '%' || #{filterData} || '%'
ORDER BY a.test_date DESC , a.test_id DESC) a ) b WHERE b.rn > (#{page} - 1) * 10
AND b.rn <= #{page} * 10

mapper.update:

		SELECT  result_id_seq.NEXTVAL
		FROM    DUAL

		DECLARE
			v_sub NUMBER(10); 
    		v_target NUMBER(10); 
    		v_pscore NUMBER(10); 
    		v_rid_cnt NUMBER(10); 
    		v_rid NUMBER(10); 
		BEGIN

			SELECT  DISTINCT a.pass_score, 
			        b.test_target_id, 
			        c.subject_id
			INTO    v_pscore, v_target, v_sub
			FROM    tests a 
					JOIN test_targets b
					ON (a.test_id = b.test_id)
					JOIN quizbox c 
					ON (b.test_id = c.test_id)
			WHERE   b.test_id = #{testId}
			AND     b.user_id = #{userId};

			SELECT  COUNT(a.result_id)
			INTO    v_rid_cnt
        	FROM    exam_results a 
        			JOIN test_targets b 
        			ON(a.test_target_id = b.test_target_id)
        	WHERE   b.test_id = #{testId}
        	AND     b.user_id = #{userId};

        	IF v_rid_cnt = 0 THEN 

				INSERT INTO exam_results(result_id
			                         	 ,subject_id
			                         	 ,pass_score
			                         	 ,test_target_id)
				VALUES(result_id_seq.NEXTVAL
			       	   ,v_sub
			      	   ,v_pscore
			           ,v_target);
				COMMIT;
			ELSE 

				SELECT  a.result_id
				INTO    v_rid
	        	FROM    exam_results a 
	        			JOIN test_targets b 
	        			ON(a.test_target_id = b.test_target_id)
	        	WHERE   b.test_id = #{testId}
	        	AND     b.user_id = #{userId};

				UPDATE  exam_results
				SET     result_score = 0
				WHERE   result_id = v_rid;
				COMMIT;

    			DELETE FROM test_results
    			WHERE result_id = v_rid;
    			COMMIT;
        	END IF;
		END;

		DECLARE
    		v_answer NUMBER(10); 
    		v_iscorrect NUMBER(10); 
    		v_tid NUMBER(10); 
    		v_rid NUMBER(10); 
		BEGIN

    		SELECT  example_num
    		INTO    v_answer
    		FROM    answers
    		WHERE   quiz_id = #{quizId}
    		AND     example_answer = 1;

    		SELECT  test_target_id
    		INTO	v_tid
    		FROM  	test_targets
    		WHERE   test_id = #{testId}
    		AND		user_id = #{userId};

    		SELECT  MAX(result_id) AS result_id
    		INTO    v_rid
    		FROM    exam_results a
    				JOIN test_targets b
    				ON(a.test_target_id = b.test_target_id)
    		WHERE 	b.test_id = #{testId}
    		AND     b.test_target_id = v_tid;

			IF v_answer = #{testAnswer} THEN
        		v_iscorrect := 1;
    		ELSE
        		v_iscorrect := 0;
    		END IF;

    		INSERT INTO test_results(test_result_id
		                         	 ,test_answer
		                         	 ,quiz_id
		                         	 ,is_correct
		                         	 ,user_id
		                         	 ,result_id)
    		VALUES(test_result_id_seq.NEXTVAL
		    	   ,#{testAnswer}
		    	   ,#{quizId}
		    	   ,v_iscorrect
		    	   ,#{userId}
		    	   ,v_rid);
    		COMMIT;
    	END;

		DECLARE
			v_rscore1 NUMBER(10,0); 
			v_rscore2 NUMBER(10,0); 
			v_pscore NUMBER(10,0); 
			v_rid NUMBER(10,0); 
			v_isreexam NUMBER(10); 
		BEGIN

			SELECT  a.result_id, a.pass_score, a.result_score
			INTO   	v_rid, v_pscore, v_rscore1
			FROM    exam_results a
					JOIN test_targets b
					ON(a.test_target_id = b.test_target_id)
			WHERE   b.test_id = #{testId}
			AND		b.user_id = #{userId};

			SELECT  SUM(CASE WHEN is_correct = 1 THEN b.quiz_score
			                                     ELSE 0 
			                                     END) AS result_score
			INTO    v_rscore2
			FROM    test_results a 
     				JOIN quizbox b 
     				ON(a.quiz_id = b.quiz_id)
			WHERE   a.result_id = v_rid
			AND		b.test_id = #{testId};

			IF v_rscore2 >= v_pscore THEN
				v_isreexam := 1;
			ELSE
			    v_isreexam := 0;
			END IF;

			UPDATE  exam_results
			SET	    result_score = v_rscore2,
			        is_reexam = v_isreexam
			WHERE   result_id = v_rid;
			COMMIT;
		END;
