mapper.select:
		SELECT  quiz_content
                ,quiz_id
                ,quiz_score
                ,subject_id
        FROM    quizbox
        WHERE   test_id = #{testId}
        AND     subject_id = #{subjectId}
        AND     quiz_id = #{quizId}

		SELECT  a.example_num
		        ,a.text_content
        FROM    answers a JOIN quizbox b
                ON(a.quiz_id = b.quiz_id)
        WHERE   a.subject_id = #{subjectId} 
        AND     b.test_id = #{testId} 
        AND     b.quiz_id = #{quizId}
        ORDER BY a.example_num

mapper.update:

		SELECT  result_id_seq.NEXTVAL
		FROM    DUAL

		DECLARE
			v_sub NUMBER(10); 
    		v_target NUMBER(10); 
    		v_pscore NUMBER(10); 
    		v_rid_cnt NUMBER(10); 
    		v_rid NUMBER(10); 
		BEGIN

			SELECT  DISTINCT a.pass_score, 
			        b.test_target_id, 
			        c.subject_id
			INTO    v_pscore, v_target, v_sub
			FROM    tests a 
					JOIN test_targets b
					ON (a.test_id = b.test_id)
					JOIN quizbox c 
					ON (b.test_id = c.test_id)
			WHERE   b.test_id = #{testId}
			AND     b.user_id = #{userId};

			SELECT  COUNT(a.result_id)
			INTO    v_rid_cnt
        	FROM    exam_results a 
        			JOIN test_targets b 
        			ON(a.test_target_id = b.test_target_id)
        	WHERE   b.test_id = #{testId}
        	AND     b.user_id = #{userId};

			SELECT  a.result_id
			INTO    v_rid
        	FROM    exam_results a 
        			JOIN test_targets b 
        			ON(a.test_target_id = b.test_target_id)
        	WHERE   b.test_id = #{testId}
        	AND     b.user_id = #{userId};

        	IF v_rid_cnt = 0 THEN 

				INSERT INTO exam_results(result_id
			                         	 ,subject_id
			                         	 ,pass_score
			                         	 ,test_target_id)
				VALUES(result_id_seq.NEXTVAL
			       	   ,v_sub
			      	   ,v_pscore
			           ,v_target);
				COMMIT;
			ELSE 
				UPDATE  exam_results
				SET     result_score = 0
				WHERE   result_id = v_rid;
				COMMIT;
        	END IF;
		END;

		DECLARE
    		v_answer NUMBER(10); 
    		v_iscorrect NUMBER(10); 
    		v_tid NUMBER(10); 
    		v_rid NUMBER(10); 
		BEGIN

    		SELECT  example_num
    		INTO    v_answer
    		FROM    answers
    		WHERE   quiz_id = #{quizId}
    		AND     example_answer = 1;

    		SELECT  test_target_id
    		INTO	v_tid
    		FROM  	test_targets
    		WHERE   test_id = #{testId}
    		AND		user_id = #{userId};

    		SELECT  MAX(result_id) AS result_id
    		INTO    v_rid
    		FROM    exam_results a
    				JOIN test_targets b
    				ON(a.test_target_id = b.test_target_id)
    		WHERE 	b.test_id = #{testId}
    		AND     b.test_target_id = v_tid;

			IF v_answer = #{testAnswer} THEN
        		v_iscorrect := 1;
    		ELSE
        		v_iscorrect := 0;
    		END IF;

    		INSERT INTO test_results(test_result_id
		                         	 ,test_answer
		                         	 ,quiz_id
		                         	 ,is_correct
		                         	 ,user_id
		                         	 ,result_id)
    		VALUES(test_result_id_seq.NEXTVAL
		    	   ,#{testAnswer}
		    	   ,#{quizId}
		    	   ,v_iscorrect
		    	   ,#{userId}
		    	   ,v_rid);
    		COMMIT;
    	END;

		DECLARE
			v_rscore NUMBER(10,0); 
			v_pscore NUMBER(10,0); 
			v_rid NUMBER(10,0); 
			v_isreexam NUMBER(10); 
		BEGIN

			SELECT  a.result_id, a.pass_score
			INTO   	v_rid, v_pscore
			FROM    exam_results a
					JOIN test_targets b
					ON(a.test_target_id = b.test_target_id)
			WHERE   b.test_id = #{testId}
			AND		b.user_id = #{userId};

			SELECT  SUM(CASE WHEN is_correct = 1 THEN b.quiz_score
			                                     ELSE 0 
			                                     END) AS result_score
			INTO    v_rscore
			FROM    test_results a 
     				JOIN quizbox b 
     				ON(a.quiz_id = b.quizbox_id)
			WHERE   a.result_id = v_rid;

			IF v_rscore >= v_pscore THEN
				v_isreexam := 1;
			ELSE
			    v_isreexam := 0;
			END IF;


			UPDATE  exam_results
			SET	    result_score = v_rscore,
			        is_reexam = v_isreexam
			WHERE   result_id = v_rid;
			COMMIT;
		END;
