mapper.insert:
	INSERT INTO comments (comment_id
                      , reply_id
                      , comment_writer
                      , comment_content
                      , comment_date)
   VALUES (#{commentId}
         , #{replyId}
         , #{commentWriter}
         , #{commentContent}
         , sysdate);

mapper.update:
UPDATE comments
SET comment_content = #{commentContent}
WHERE comment_id = #{commentId}
