mapper.insert:
	INSERT INTO comments (comment_id
                      , reply_id
                      , comment_writer
                      , comment_content
                      , comment_date)
   VALUES (comment_id_seq.nextval
         , #{replyId}
         , #{commentWriter}
         , #{commentContent}
         , sysdate)

mapper.update:
UPDATE comments
SET comment_content = #{commentContent}, comment_date = sysdate
WHERE comment_id =#{commentId}
