mapper.select:
SELECT c.class_id, h.homework_id, h.homework_content, h.homework_date, h.homework_title, s.subject_name FROM homeworks h JOIN curriculum c ON (h.curriculum_id = c.curriculum_id) JOIN subjects s ON (h.curriculum_id = s.curriculum_id) WHERE h.subject_id = s.subject_id
AND class_id = #{classId}
ORDER BY h.homework_id DESC

SELECT c.class_id, h.homework_id, h.homework_title, s.subject_name, h.homework_date FROM homeworks h JOIN curriculum c ON(h.curriculum_id = c.curriculum_id) JOIN subjects s ON(c.curriculum_id = s.curriculum_id) JOIN users u ON (s.curriculum_id = u.curriculum_id) WHERE h.subject_id = s.subject_id AND user_id = #{userId}
AND s.subject_id = #{subjectId}
ORDER BY h.homework_id DESC
