mapper.insert:

INSERT INTO users (
	user_id
	, id
	, password
	, name
	, email
	, tel
	, address
	, user_image
	, company_info
	, user_type
	, curriculum_id
) VALUES (
	user_id_seq.NEXTVAL
	, #{id}
	, #{password}
	, #{name}
	, #{email}
	, #{tel}
	, #{address}
	, #{userImage}
	, #{companyInfo}
	, #{userType}
	, #{curriculumId}
)

mapper.select:
SELECT id
     , password
     , name
FROM users
WHERE id = #{id}
AND password = #{password}

SELECT user_id , user_image , user_type , name , tel , email FROM (SELECT rownum rn, b.* FROM users b
WHERE rownum <= (#{page} * 5)
) a WHERE (a.rn > (#{page} - 1) * 5)
