mapper.insert:

INSERT INTO users (
	user_id
	, id
	, password
	, name
	, email
	, tel
	, address
	, user_image
	, company_info
	, user_type
	, curriculum_id
) VALUES (
	user_id_seq.NEXTVAL
	, #{id}
	, #{password}
	, #{name}
	, #{email}
	, #{tel}
	, #{address}
	, #{img}
	, #{companyInfo}
	, #{userType}
	, #{curriculumId}
)

mapper.select:
SELECT id
     , password
     , name
FROM users
WHERE id = #{id}
AND password = #{password}
